import React, { useEffect } from 'react';
import '../css/Endmessage.css';

interface messageProps{
    count: number;
    goal: number;
    clearFlug: boolean;
    setClearFlug: Function;
    allreset: () => void;
    restart: () => void;
}

const Endmessage:React.FC<messageProps> = ({count, goal, clearFlug, setClearFlug, allreset, restart }) => {
    useEffect(()=>{
        if(count === goal){
            setClearFlug(true);
        };
    },[count])

  return (
    <>
        {clearFlug && (
            <>
                <div className='message' >計算遅いね。がっかりしたよ。</div>
                <button onClick={restart}>同条件で再挑戦</button>
                <button onClick={allreset}>違う条件で再挑戦</button>
            </>
        )}
    </>
  )
}

export default Endmessage
