import React, {useEffect, useState} from 'react';
import { Photos } from "./import";
import "../css/Library.css";

const Library = () => {

    const [displayNumber, setDisplayNumber] = useState<null | number>(null);

    const display = (value: number) => {
        return ()=> {
            setDisplayNumber(value);
        }
    }

    const closeButton = () => {
        setDisplayNumber(null);
    }


  return (
    <>
        <h2 className="italic text-4xl ml-3 mt-3.5">Library</h2>
        <div className="wrapper">
            {Photos.map((photo, index) => (
                <div className="photo" key={index}>
                    <img src={photo} alt="" onClick={display(index)}/>
                </div>
            ))}
            {displayNumber !== null && (
                <div>
                    <button onClick={closeButton}>Ã—</button>
                    <img src={Photos[displayNumber]} alt="" />
                </div>
            )}
        </div>
    </>
  )
}

export default Library;
